
public class RelationalOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Relational operator ==,!=,<,>,<=,>=
       int a=25,b=36;
       System.out.println("Greater then is:" +(a>b));
       System.out.println("lessthen then is:" +(a<b));
       System.out.println("double equal to is:" +(a==b));
       System.out.println("greater then or equal to :"+(a>=b));
       System.out.println("Less then or equal to :" +(a<=b));
		
		
		
		
	}

}
