package javaproject;

public class Switchdemo {

	public static void main(String[] args) {
		// TODO Auto-generated method 
		
		String  car="BMW";
		switch(car) {
		case   "Merco":
		System.out.println("Excelent merco");
		break;
		case "Tesla":
		System.out.println("Auto pilot american car");
		case "BMW":
		System.out.println("Supper german car");
		break;
		case "VW":
		System.out.println("Fun to drive");
		break;
		default:
		System.out.println("None of the cond match");
		
		}
	}

}
